+            value: plugin,
+            type: 0,
+            schema: stateValidationSchema,
+          });
+        }
+      } else {
+        try {
+          await dispatchWorkflow(context, {
+            owner: plugin.owner,
+            repository: plugin.repo,
+            workflowId: "validate-schema.yml",
+            ref: plugin.ref,
+            inputs: inputs.getWorkflowInputs(),
