+  const {
+    context: {
+      logger,
+      payload: {
+        repository: {
+          owner: { login },
+        },
+      },
+    },
+  } = params;
+
+  if (!issueComments) {
+    logger.info("No comments found on issue");
+    return linkedIssues;
+  }
+
+  for (const comment of issueComments) {
+    const linkedIssue = idIssueFromComment(login, comment.body);
+    if (linkedIssue) {
+      linkedIssues.push(linkedIssue);
